    color: #900;
  }
  .header,
  .site-header {
    background-color: #234;
    border-bottom: 1px solid #E5E5E5;
    color: #ddd;
  }
  .site-footer {
    color: #ccc;
  }
  .protip {
    margin-top: 20px;
    margin-bottom: 20px;
